mapper.select:
select s.*, m.picture, h.tag_name from shop_info s left join member m on s.id = m.id left join shophashtag t on s.id = t.member_id left join hashtag h on t.tag_id = h.tag_id
s.location_x < ${maxLocationX} and s.location_X > ${maxLocationXa} and s.location_y < ${maxLocationY} and s.location_y > ${maxLocationYa} and s.id != #{id} and s.address is not null and h.tag_name in
#{tags}

mapper.update:
update shop_table_setting set
table_name = #{tableName}, allow_visitor = #{allowVisitor}, allow_start = #{allowStart}, allow_end = #{allowEnd}, time_div = #{timeDiv}, time_max = #{timeMax},
memo = #{memo}, enable = #{enable} where table_id = #{tableId}

update shop_table_reservation
set reservation_status = '예약취소'
where reservation_no = #{reservationNo}

update shop_table_price
set price = #{price}
where table_id = #{tableId}

update reservation_req_price
set restprice = restprice - #{amount}
where
	reservation_no = #{reservationNo}

update shop_table_reservation_share
	set req_dutchpay = #{status}
where
	reservation_no = #{reservationNo}

	and
	attend_user = #{id}

update shop_table_reservation_share
set review_status = 'S'
where
	attend_user = #{attendUser}
	and
	reservation_no = #{reservationNo}
